import React, {useState,useEffect} from 'react';
import Select from 'react-select';
import { Button,Container,Row,Col } from 'reactstrap';
import './search.css';



function Search() {
  const [list, setList] = useState([])


  useEffect(()=>{
    fetch('https://randomuser.me/api/?results=50')
    .then(res => {
      return res.json()
    })
    .then(response => {
      setList(response.results)
    })
  },[])

  const options =  list.map(item =>{
    return{picture:item.picture.thumbnail, value:item.name.title+" "+ item.name.first+" "+ item.name.last, label:item.name.title+" "+ item.name.first+" "+ item.name.last};
  })


  const CustomOptions = ({innerProps, isDisabled}) =>
  !isDisabled ? (
    <div {...innerProps}>{options}</div>
 ) : null;


  // const formatOptionLabel = ({ picture, value, label,}) => (
  //   <div style={{ display: "flex" }}>
  //     <img src={picture}></img>
  //     <div style={{ marginLeft: "10px", color: "#ccc" }}>
  //       {label}
  //     </div>
  //   </div>
  // );

  return (
    <Container className="container-content" fluid="sm">
      <Row>
        <Col sm="12" md={{ size: 8, offset: 2 }}>
          <Select placeholder="Pilih Personalia..." className="search-box"  components={options} />
        </Col>
      </Row>
    </Container>
  );
}

export default Search;
